@app.get("/test_db", endpoint="test_db")
def test_db():
    eng = get_engine()
    payload = {"ok": False, "dialect": eng.dialect.name}

    try:
        with eng.begin() as conn:
            # Basic connection info
            payload["version"] = conn.exec_driver_sql("SELECT version()").scalar()
            payload["current_database"] = conn.execute(text("SELECT current_database();")).scalar()

            # Check if schema exists
            schema_exists = conn.execute(text(
                "SELECT EXISTS (SELECT 1 FROM information_schema.schemata WHERE schema_name = 'ns');"
            )).scalar()
            payload["schema_exists"] = schema_exists

            if schema_exists:
                # Set search path
                conn.execute(text("SET search_path TO ns, public;"))

                # Check which tables exist
                tables = conn.execute(text(
                    "SELECT table_name FROM information_schema.tables WHERE table_schema = 'ns';"
                )).fetchall()
                payload["tables"] = [t[0] for t in tables]

                # Counts (only if tables exist)
                if "coordinates" in payload["tables"]:
                    payload["coordinates_count"] = conn.execute(text("SELECT COUNT(*) FROM ns.coordinates")).scalar()
                    try:
                        rows = conn.execute(text(
                            "SELECT study_id, ST_X(geom) AS x, ST_Y(geom) AS y, ST_Z(geom) AS z FROM ns.coordinates LIMIT 3"
                        )).mappings().all()
                        payload["coordinates_sample"] = [dict(r) for r in rows]
                    except Exception as e:
                        payload["coordinates_sample"] = []
                        payload["coordinates_error"] = str(e)

                if "metadata" in payload["tables"]:
                    payload["metadata_count"] = conn.execute(text("SELECT COUNT(*) FROM ns.metadata")).scalar()
                    try:
                        rows = conn.execute(text("SELECT * FROM ns.metadata LIMIT 3")).mappings().all()
                        payload["metadata_sample"] = [dict(r) for r in rows]
                    except Exception as e:
                        payload["metadata_sample"] = []
                        payload["metadata_error"] = str(e)

                if "annotations_terms" in payload["tables"]:
                    payload["annotations_terms_count"] = conn.execute(text("SELECT COUNT(*) FROM ns.annotations_terms")).scalar()
                    try:
                        rows = conn.execute(text(
                            "SELECT study_id, contrast_id, term, weight FROM ns.annotations_terms LIMIT 3"
                        )).mappings().all()
                        payload["annotations_terms_sample"] = [dict(r) for r in rows]
                    except Exception as e:
                        payload["annotations_terms_sample"] = []
                        payload["annotations_terms_error"] = str(e)
            else:
                payload["message"] = "Schema 'ns' does not exist. Run create_db.py to populate the database."

        payload["ok"] = True
        return jsonify(payload), 200

    except Exception as e:
        payload["error"] = str(e)
        return jsonify(payload), 500

